import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
    {
        path: '/login',
        name: 'login',
        component: () => import('../views/login/index.vue')
    },
    {
        path: '/',
        name: 'Layout',
        redirect: "/home",
        component: () => import('../views/Layout/index.vue'),
        children: [
            {
                path: "/home",
                name: 'home',
                component: () => import('../views/home/index.vue'),
                meta:{
                    title:"扶뒬못"
