import request from '../utils/axios.js'

//游戏名称下拉
export function getGameNameList(query) {
    return request({
        url: '/api/gameManage/list',
        method: 'get',
        params: query
    });
}

//游戏列表数据
export function getDataList(query) {
    return request({
        url: '/api/game/data',
        method: 'get',
        params: query
    });
}

//游戏状态修改
export function editgameState(data) {
    return request({
        url: '/api/game/edit',
        method: 'post',
        data: data
    });
}

//游戏详情数据
export function getDetailsDataList(query) {
    return request({
        url: '/api/game/details',
        method: 'get',
        params: query
    });
}
